mapper.select:
SELECT * FROM( SELECT *, ROW_NUMBER() OVER(ORDER BY reg_date) as num FROM board_${url}
WHERE id LIKE CONCAT('%', #{search}, '%')
WHERE bname LIKE CONCAT('%', #{search}, '%')
WHERE ( id LIKE CONCAT('%', #{search}, '%') OR bname LIKE CONCAT('%', #{search}, '%') )
ORDER BY reg_date DESC ) add_rn_board
WHERE num > (#{pageNum} - 1)*#{amount} and num <= #{pageNum}*#{amount}

mapper.update:
UPDATE board_${url}
SET bname = #{bname} , content = #{content}
WHERE bno = #{bno}

UPDATE board_${url}
SET cnt = cnt + 1
WHERE bno = #{bno}

UPDATE board_${url}
SET 

	
		like_ = like_ + 1
	
	
		like_ = like_ - 1
	

WHERE
bno=#{bno}
